+ (BOOL)isValidateEmail:(NSString *)email{
    
    NSString *emailRegex = @"[A-Z0-9a-z._%+-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,4}";
    
    NSPredicate *emailPredicate = [NSPredicate predicateWithFormat:@"SELF MATCHES %@", emailRegex];
    
    return [emailPredicate evaluateWithObject:email];
    
}

+ (NSString *)getDefaultCountryCode {
    //First step: get device info.
    NSString *ISOcountryCode;
    
    CTTelephonyNetworkInfo *network_Info = [CTTelephonyNetworkInfo new];
    CTCarrier *carrier = network_Info.subscriberCellularProvider;
    ISOcountryCode = carrier.isoCountryCode;
    
    if (ISOcountryCode.length == 0) {
        NSLocale *locale = [NSLocale currentLocale];
        ISOcountryCode = [locale objectForKey:NSLocaleCountryCode];
    }
    
    ISOcountryCode = [ISOcountryCode uppercaseString];
    
    //Second step: get area code by ISOcountryCode.
    NSString *code = @"";
    NSArray *countryCodeList = [TYDemoLoginAndRegisterUtils getDefaultPhoneCodeList];
    for (NSDictionary *dict in countryCodeList) {
        if ([[dict objectForKey:@"a"] isEqualToString:ISOcountryCode]) {
            code = dict[@"c"];
        }
    }
    return code;
}

